mapper.select:
select
`user_id`,`username`,`name`,`password`,`dept_id`,`email`,`mobile`,`status`,`user_id_create`,`gmt_create`,`gmt_modified`,`sex`,`birth`,`pic_id`,`live_address`,`hobby`,`province`,`city`,`district`
from sys_user

	and user_id = #{userId}
	and username = #{username}
	and name = #{name}
	and password = #{password}
	and dept_id = #{deptId}
	and dept_id in

			#{item}


	and email = #{email}
	and mobile = #{mobile}
	and status = #{status}
	and user_id_create = #{userIdCreate}
	and gmt_create = #{gmtCreate}
	and gmt_modified = #{gmtModified}
	and sex = #{sex}
	and birth = #{birth}
	and pic_id = #{picId}
	and live_address = #{liveAddress}
	and hobby = #{hobby}
	and province = #{province}
	and city = #{city}
	and district = #{district}



		order by ${sort} ${order}


		order by user_id desc



	limit #{offset}, #{limit}
